import React, { useState } from 'react';
import {
  Conteudo,
  Paragrafo,
  Botao, AreaBotao,
  Link,
  AreaTexto,
  AreaImg,
} from "./estilo.js";

import { FiArrowRightCircle, FiArrowLeftCircle } from "react-icons/fi";
import bb8 from "../../../assests/bb8.png";


import Robo from "../Robo"
import Ansiedade from "../Ansiedade"
import Especialista from "../Especialista"



export default function Inicio() {
  const [loading, setLoading] = useState(false);
  const [next, setNext] = useState(false);
  const [back, setBack] = useState(false);
  const [neutro, setNeutro] = useState(false);
  const [boot, setBoot] = useState(true);



  window.watsonAssistantChatOptions = {
    integrationID: "f1caa9b9-0a8f-42ac-a1fb-5d4e050a6219", // The ID of this integration.
    region: "us-south", // The region your integration is hosted in.
    serviceInstanceID: "7ec9f572-73ef-4987-a133-f55f02f4ec8c", // The ID of your service instance.
    onLoad: function(instance){() => {
      instance.render();

      if (boot === true) {
        instance.openWindow()
      } else {
        instance.closeWindow();

      }


    }
  }
  };
  setTimeout(function () {
    const t = document.createElement('script');
    t.src = "https://web-chat.global.assistant.watson.appdomain.cloud/loadWatsonAssistantChat.js";
    document.head.appendChild(t);
  });

  function conhecer() {
    setBoot(true)

  }






  function proximo() {
    setLoading(true);
    setNext(true)
    setBack(false)
  }

  function anterior() {
    setLoading(true)
    setBack(true);
    setNext(false);
  }


  if (loading) {
    return (
      <div>

        {next &&

          < Robo carregando={setLoading}
            proximo={setNext}
            anterior={setBack}
            neutro={setNeutro}
          />

        }
        {back &&


          < Ansiedade carregando={setLoading}
            proximo={setNext}
            anterior={setBack}
            neutro={setNeutro}
          />


        }
        { neutro &&


          <  Especialista carregando={setLoading}
            proximo={setNext}
            anterior={setBack}
            neutro={setNeutro}
          />

        }


      </div>

    );

  }






  return (
    <Conteudo  >

      <AreaTexto>

        <Paragrafo>

          Oi, seja bem vindo(a). Meu nome é VB-2 sou irmão do BB-8, isso mesmo,
          meu irmão é famoso, participou do filme do Star Wars.
          Meu sonho é participar da comunidade onde todos ajudam um ao outro
          e não sintam mais sozinhos. Se desejar conhecer mais sobre ANSIEDADE,
          algo que me afeta constantemente, clique nas setas abaixo ou no
          botão para conversar comigo.

      </Paragrafo>

      </AreaTexto>

      <AreaImg>

        <img src={bb8} title="Amigo" alt="Robo" />

      </AreaImg>

      <Link onClick={proximo}>

        <FiArrowRightCircle size='30px' /> Sobre VB-2!

      </Link>

      <Link onClick={anterior}>

        <FiArrowLeftCircle size='30px' /> Ansiedade

      </Link>

      <AreaBotao>

        <Botao  >Conhecer?</Botao>

      </AreaBotao>

    </Conteudo>
  );
}
