import styled, { keyframes, css } from 'styled-components';



const animate = keyframes`
 from{
    opacity: 0;
  }
  to{
    opacity:1;
  }
`;

export const Conteudo = styled.div` 
 display:flex;
 width:100%;
 flex-direction:column;
 justify-content:center;
 align-items:center;
 height:100vh;
 animation: ${animate} 3s linear;
 `;

export const Titulo = styled.h1`
  width:100%;
  text-align:center;
  font-style: normal;
  font-weight: normal;
  font-size: 48px;
  line-height: 58px;
  height:100px;
`;

export const Paragrafo = styled.p`
  display:flex;
  width:90%;
  font-style: normal;
  font-weight: normal;
  font-size: 28px;
  line-height: 31px;

`;


export const AreaBotao = styled.div`
 display:flex;
 width:90%;
 justify-content:space-between;
 height:200px;
 align-items:flex-end;
a{
  text-decoration:none;
  font-family:"Fira Sans",sans-serif;
  font-style: normal;
  font-weight: normal;
  border-radius:21px;
  font-size: 20px;
  line-height: 23px;
  color:black;
  transition:1s;

}
a:hover{
  text-decoration:underline;
  color:#8B4513;
  font-size:150%;
}

`;

const animate2 = keyframes`          
from{
  transform: rotate(0deg)
  width:100px;
  height:30px;
  border-radius:10px;
  font-size: 10px;
  line-height: 13px;

}
to{
  transform:rotate(360deg)
} 

`;



export const Botao = styled.button`
width: 268px;
height: 62px;
background: #9AFBF5;
font-family:"Fira Sans",sans-serif;
font-style: normal;
font-weight: normal;
border-radius:21px;
font-size: 20px;
line-height: 23px;
animation:${animate2} 3s linear ;


`;
