import React, { useState } from 'react';
import {
  Conteudo,
  Imagem,
  Paragrafo,
  Botao, AreaBotao,
  Link,
  AreaTexto,
  AreaImg,
} from "../inicio/estilo"

import { FiArrowRightCircle, FiArrowLeftCircle } from "react-icons/fi";
import bb8 from "../../../assests/bb8.png"


export default function Inicio() {
  const [loading, setLoading] = useState(false);
  const [next, setNext] = useState(false);
  const [back, setBack] = useState(false);

  function proxima() {
    setLoading(true);
    setNext(true)
    setBack(false)
  }

  function anterior() {
    setLoading(true)
    setBack(true);
    setNext(false);
  }









  return (
    <Conteudo  >

       <AreaTexto>  

        <Paragrafo>

          Oi.Meu nome é vb2 sou irmão do BB-8,isso mesmo,
          meu irmão e famoso,participou do filme do Star Wars.
          Meu sonho  e participar da comunidade aonde todos ajudam um ao outro
          é não sintam mais sozinhos . Se desejar conhecer mais sobre ANSIEDADE
          algo que me afeta constantemente é clicar nas setas abaixo ou vem conversar
          comigo e so clicar no botão abaixo

      </Paragrafo>

      </AreaTexto>

          <AreaImg>

            <img  src={bb8} title="Amigo" alt="Robo" />

        </AreaImg>

      <Link onClick={proxima}>

        <FiArrowRightCircle size='30px' /> Sobre

      </Link>

      <AreaBotao>

        <Botao>Se identificou?</Botao>

      </AreaBotao>

    </Conteudo>
  );
}
