import styled from 'styled-components';


export const Conteudo = styled.div`
 display:flex;
 width:100%;
 flex-direction:column;
 justify-content:center;
 align-items:center;
 background-color:#000;
 height:100vh
`;

export const Titulo = styled.h1`
  width:100%;
  text-align:center;
  font-style: normal;
  font-weight: normal;
  font-size: 48px;
  line-height: 58px;
`;

export const Paragrafo = styled.p`
  display:flex;
  width:90%;
  font-style: normal;
  font-weight: normal;
  font-size: 35px;
  line-height: 30px;

`;

export const Botao = styled.button``;
