import React, { useEffect, useState } from "react"

import List from "../List"
import './estilos.css'


export default function Conversa() {
    const [boot, setUser] = useState([
        { text: 'Seja bem vindo(a),vamos conversar por aqui meu canal preferido' }

    ])
    const [user, setBoot] = useState([''])
    const [input, setInput] = useState('')

    useEffect(() => {
        {
            boot.map((item) => {
                return (
                    <List value={item.text} />
                )
            })
        }
        {
            user.map((item) => {
                return (
                    <List value={item.text} />
                )
            })
        }
    }, [user, boot.text])

    async function send() {
        if (input === '') {
            alert('Voce não digitou nada')
            return;
        }
        console.log(input)
    }



    return (
        <div className="container">

            <input
                type="text"
                placeholder="digite algo para conversarmos"
                value={input}
                onChange={(item) => setInput(item.target.value)}
                className="input"
            />
            <button className="button" onClick={send} >
                Enviar
            </button>

        </div>

    );



}
