import styled, { keyframes} from 'styled-components';



const animate = keyframes`
 from{
    opacity: 0;
  }
  to{
    opacity:1;
  }
`;

export const Conteudo = styled.div` 
 display:flex;
 width:100%;
 flex-direction:column;
 justify-content:center;
 align-items:center;
 height:100vh;
 animation: ${animate} 3s linear;
 `;

export const Titulo = styled.h1`
  width:100%;
  text-align:center;
  font-style: normal;
  font-weight: normal;
  font-size: 48px;
  line-height: 58px;
  height:100px;
  text-shadow:1px 1px 0px #fff;
`;

export const Paragrafo = styled.p`
  display:flex;
  width:90%;
  font-style: normal;
  font-weight: normal;
  font-size: 28px;
  line-height: 31px;
  opacity:0.7;

`;


export const AreaBotao = styled.div`
 display:flex;
 width:90%;
 justify-content:space-between;
 height:200px;
 align-items:flex-end;
a{
  text-decoration:none;
  font-family:"Fira Sans",sans-serif;
  font-style: normal;
  font-weight: normal;
  border-radius:21px;
  font-size: 20px;
  line-height: 23px;
  color:black;
  transition:1s;

}
a:hover{
  text-decoration:underline;
  color: #EEB422;
  font-size:150%;
}

`;



export const Botao = styled.button`
width: 268px;
height: 62px;
background: #B5B5B5;
font-family:"Fira Sans",sans-serif;
font-style: normal;
font-weight: normal;
border-radius:21px;
font-size: 20px;
line-height: 23px;
cursor:pointer;
transition:all 0.3s ease-in;
:hover{
  background-color:  #EEB422;
            color:white; 
  width:300px;
  height:90px;
  color:black;
}


`;
