export default function Conversa() {
    const [user, setUser] = useState([
        { text: 'eu gosto muito de comer maça' },
        { text: 'eu gosto muito de comer doce' },
        { text: 'eu gosto muito de comer macarrao' },
    ])
    const [boot, setBoot] = useState([

        {  text: 'eu gosto muito de comer pera' },

        {  text: 'eu gosto muito de comer arroz' }
    ])         

   useEffect(()=>{
      setBoot([])
      setUser([])

      user.map((item)=>{
        let list={    
          item:item.text
        }
        setBoot(oldArray=>[...oldArray,list])   
      })
      boot.map((item)=>{
        let list={    
          item:item.text
        }
        setUser(oldArray=>[...oldArray,list])    
      })

   },[user,boot])  
   useEffect(()=>{
       user
       boot

   },[])



    return (
        <div>

          <text>  {user.text} </text>
          <text> {boot.text}</text> 

        </div>

    );



}
