import styled, { keyframes, css } from 'styled-components';



const animate = keyframes`
 from{
    opacity: 0;
  }
  to{
    opacity:1;
  }
`;

export const Conteudo = styled.div` 
 display:flex;
 width:100%;
 flex-direction:column;
 justify-content:center;
 align-items:center;
