import React, { useState } from "react"

import List from "../List"
import './estilos.css'
import api from "../../../services/api"


export default function Conversa() {
    const [boot, setUser] = useState('')
    const [user, setBoot] = useState('')
    const [input, setInput] = useState('')




    async function send() {
        if (input === '') {
            alert('Voce não digitou nada')
            return;
        }
        try {
            const response = await api.get(`/?mensagem=${input}`);
            setBoot(input)
            setUser(response.data)
            console.log(response.data)
        } catch (error) {
            alert(error)
        }


    }



    return (
        <div className="container">
            { user &&

               <div className="container">

                <List value={user} boot={boot} />

                </div>     

            }


            <input
                type="text"
                placeholder="digite algo para conversarmos"
                value={input}
                onChange={(item) => setInput(item.target.value)}
                className="input"
            />
            <button className="button" onClick={send} >
                Enviar
           </button>


        </div>

    );



}
