        {  text: 'eu gosto muito de comer pera' },

        {  text: 'eu gosto muito de comer arroz' }
    ])
    const [arrayBot,setArrayBot]=useState([])
    const [arrayUser,setArrayUser] = useState([])          

   useEffect(()=>{
      setBoot(boot)
      setUser(user)


   },[setBoot,setUser])  



