        <div>

            {user.map((item) => {
                return (

                    setArrayBot([...item,arrayBot])

                )

            })}
            {boot.map((item) => {
                return (

                    setArrayUser([...item,arrayUser])

                )
            })

            }

        </div>

    );



}
